INSERT INTO web_app.movie (pk_movie_id, name, year, running_time, banner_link, about) VALUES (9, 'Dune', 2021, 155, 'http://localhost:8080/images/dune.jpg', 'Kultovní sci-fi dílo vypráví o mocenských bojích uvnitř galaktického Impéria, v nichž jde o ovládnutí planety Arrakis: zdroje vzácného koření - melanže, jež poskytuje zvláštní psychické schopnosti, které umožňují cestování vesmírem. Padišáh imperátor svěří správu nad Arrakisem a s ní i komplikovanou těžbu neobyčejné látky vévodovi rodu Atreidů. Celá anabáze je ale součástí spiknutí, z něhož se podaří vyváznout jen vévodovu synovi Paulovi a jeho matce, kteří uprchnou do pouště. Jejich jedinou nadějí jsou nedůvěřiví domorodí obyvatelé fremeni, schopní trvale přežít ve vyprahlé pustině. Mohl by Paul Atreides být spasitelem, na kterého tak dlouho čekají?');
INSERT INTO web_app.movie (pk_movie_id, name, year, running_time, banner_link, about) VALUES (10, 'Venom', 2018, 112, 'http://localhost:8080/images/venom.jpg', 'Na filmová plátna přichází jedna z nejlepších a nejkomplexnějších comicsových postav společnosti Marvel. Novinář Eddie Brock (Tom Hardy) se stává hostitelem mimozemského symbionta Venoma. Brock se v rámci své práce novináře pokouší již po celé roky odhalit veřejnosti skutečnou tvář známého vědce společnosti Life Foundation Carltona Drakea (Riz Ahmed) – jeho odhodlání se přerodilo v posedlost, která mu zlikvidovala kariéru a zničila vztah s jeho přítelkyní Anne (Michelle Williams). Když do svého těla Eddie vstřebá jeden z Drakeových experimentů – mimozemšťana Venoma – zničehonic zjišťuje, že disponuje novými superschopnostmi a má možnost dělat, cokoliv se mu zachce. Zvrhlý, temný, nepředvídatelný a zuřivý Venom má za následek, že se Eddie zoufale pokouší udržet pod kontrolou své nebezpečné schopnosti, které ho současně velice přitahují a opájejí. Jelikož Eddie a Venom navzájem jeden druhého potřebují k dosažení svých cílů, jejich vzájemné pouto a propojení se prohlubuje – až přestává být jasné, kde vlastně končí Eddie a začíná Venom.');
INSERT INTO web_app.movie (pk_movie_id, name, year, running_time, banner_link, about) VALUES (11, 'Star Wars: Epizoda III - Pomsta Sithů', 2005, 140, 'http://localhost:8080/images/star_wars_3.png', 'Válka mezi povstalci a Republikou pokračuje. Generálovi Grievousovi se podařilo unést kancléře Palpatinea a rytíři Jedi, Obi-wan Kenobi a Anakin Skywalker se vydávají na palubu Grievousovy lodi kancléře osvobodit. Poté, co vniknou na loď, narazí na hraběte Dooku. Dojde mezi nimi k souboji, ve kterém Anakin Dookua porazí, a chystá se ho zajmout. Ještě svázaný Palpatine však Anakina přemluví, aby Dookua na místě zabil. Je to něco, co by správný Jedi neměl nikdy udělat. Anakin však přemlouvání podlehne. I když rytíři Jedi Palpatina zachránili, jsou vzápětí znovu zajati vojáky generála Grievouse. Ten ale rytíře Jedi podcenil, a tak se situace brzy obrátí a je to právě generál, kdo z lodi, kterou opět ovládli Jediové, musí prchnout v záchranném modulu. Anakin rozpadající se Grievousovu loď přivede bezpečně na letiště a rázem se stane hrdinou a tím, kdo zachránil kancléře. Kancléř zahrnuje Anakina svým přátelstvím a ostatní Jediové cítí, že něco není v pořádku. Anakina pronásledují zlé sny, v nich se mu zdá, jak jeho milovaná Padmé (Amidala) umírá při porodu jejich dětí. Anakin se se svými obavami obrátí na Yodu se žádostí o pomoc. Yodovy rady mu však nepomohou a ještě více posílí Anakinovy pochybnosti ohledně chování Jediů, kteří po něm chtějí, aby jim donášel informace o chování kancléře. Dalším šokem pro Anakina je zjištění, že i Padmé pochybuje o současném fungování Republiky.');
INSERT INTO web_app.movie (pk_movie_id, name, year, running_time, banner_link, about) VALUES (12, 'Pán prstenů: Společenstvo Prstenu', 2001, 172, 'http://localhost:8080/images/lotr_1.png', 'V dávných dobách byl vykován kouzelný prsten, který vlastnil pán Mordoru Sauron. Jeho moc začal využívat k šíření zla, ale o prsten nakonec v boji přišel, a ten na dlouhá léta zmizel. Nakonec ho našel hobit Bilbo Pytlík, který díky němu přestal stárnout. Na naléhavou žádost čaroděje Gandalfa předá prsten synovci Frodovi. Ten se svými kamarády Samem, Smíškem a Pipinem odcházejí do Hůrky a Gandalf se vydává pro radu za svým učitelem, čarodějem Sarumanem. Ten se však přidal na stranu zla a zajme ho. S pomocí tajemného hraničáře, přezdívaného Chodec, Frodo a jeho kamarádi uniknou jen o vlásek devíti černým jezdcům, kteří vyrazili z Temné věže, aby prsten našli a přinesli svému pánovi Sauronovi. Do Roklinky je svolána velká porada lidí a elfů, která rozhodne, že prsten musí být zničen. To je možné pouze tam, kde byl prsten zrozen, v ohni Hory osudu. Odvážný Frodo se nabídne, že tam prsten odnese. Nebezpečí je však příliš veliké, a tak se mu, jako jeho ochránci, postaví po bok čaroděj Gandalf, trpaslík Gimli, elf Legolas, bojovník Boromir, hobiti Sam, Smíšek a Pipin a také Chodec. Zrodí se Společenstvo Prstenu, které se vydává na nebezpečnou cestu plnou nástrah a nebezpečí.');
INSERT INTO web_app.movie (pk_movie_id, name, year, running_time, banner_link, about) VALUES (53, 'The Matrix', 1999, 136, 'http://localhost:8080/images/matrix_1.png', 'Za vším hledej Matrix. Zdál se vám někdy sen, který působil naprosto skutečně? Co kdybyste se nemohli probudit? Jak poznáte rozdíl mezi sněním a realitou? Když záhadná kráska Trinity (Carrie-Anne Moss) zavede počítačového hackera jménem Neo (Keanu Reeves) do tajemného paralelního světa, odhalí mu šokující pravdu - svět je podvod, propracovaný klam překroucený všemocnými stroji umělé inteligence, která nás ovládá. Neo se přidává k legendárnímu vůdci odboje Morfeovi (Laurence Fishburne) v bitvě za zničení iluze zotročující lidstvo. Každý pohyb, každá sekunda a každá myšlenka znamená boj o přežití - o únik z Matrixu.');
